CREATE MEMORY TABLE location (ID BIGINT GENERATED BY DEFAULT AS IDENTITY, name VARCHAR(256), latitude DOUBLE, longitude DOUBLE);
CREATE MEMORY TABLE rated_location (ID BIGINT GENERATED BY DEFAULT AS IDENTITY, name VARCHAR(256), latitude DOUBLE, longitude DOUBLE, rating DOUBLE);
CREATE MEMORY TABLE location_ratedlocations (ID BIGINT GENERATED BY DEFAULT AS IDENTITY, location_id BIGINT, rated_location_id BIGINT);
